SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";
CREATE DATABASE IF NOT EXISTS `timetravel` DEFAULT CHARACTER SET latin1 COLLATE latin1_swedish_ci;
USE `timetravel`;

CREATE TABLE IF NOT EXISTS `latin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `value` varchar(50) COLLATE latin1_german1_ci NOT NULL,
  `type` enum('sentence','verb','nomen') COLLATE latin1_german1_ci NOT NULL,
  `translation` varchar(50) COLLATE latin1_german1_ci NOT NULL,
  `forms` text COLLATE latin1_german1_ci NOT NULL COMMENT 'json',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COLLATE=latin1_german1_ci ROW_FORMAT=COMPACT AUTO_INCREMENT=47 ;

